import { Component, OnInit } from '@angular/core';
import { SharedService } from '../services/shared.service';
import { Card } from '../models/card.model';
import { delay } from 'rxjs/dist/types/operators';

@Component({
  selector: 'app-war-game',
  templateUrl: './war-game.component.html',
  styleUrls: ['./war-game.component.css']
})
export class WarGameComponent implements OnInit {

  private test;

  constructor(public service: SharedService) { }

  ngOnInit(): void {
    this.pullDeck();
  }
  onPlayButtonClicked() {
    alert("You clicked the play button");
  }

  async pullDeck() {
    this.test = this.service.getAllCards();
    await delay(3000);
    this.test.subscribe(response => {
      console.log(response);
    })
  }
}
